// frontend/src/components/CertificateList.js
import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import API from "../api";

export default function CertificateList() {
  const [all, setAll] = useState([]);
  const [filter, setFilter] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    API.get("/certifications").then(r => setAll(r.data));
  }, []);

  const filtered = all.filter(c =>
    c.certification_id.includes(filter) ||
    (c.denominação && c.denominação.includes(filter))
  );

  return (
    <div className="card">
      <h2>Certificados</h2>
      <input
        type="text"
        placeholder="Filtrar por ID ou denominação"
        value={filter}
        onChange={e => setFilter(e.target.value)}
      />
      <ul>
        {filtered.map(c => (
          <li key={c.id}>
            <Link to={`/certifications/${c.certification_id}`}>
              {c.certification_id}
              {c.denominação ? ` — ${c.denominação}` : ""}
            </Link>
          </li>
        ))}
      </ul>
      <button onClick={() => navigate(-1)}>Voltar</button>
    </div>
  );
}
