import React from "react";
import { showToast } from "../toast";

export default function CertificateInfo({ data }) {
  const { certificate, points } = data;

  const copiarID = () => {
    navigator.clipboard.writeText(certificate.certification_id);
    showToast("ID da certificação copiado!");
  };

  return (
    <div style={{ marginTop: 16, padding: 16, border: "1px solid #4caf50", borderRadius: 6, background: "#f6ffed" }}>
      <h3>Detalhes da Certificação</h3>
      <ul>
        <li>
          <strong>ID:</strong> {certificate.certification_id}{" "}
          <button onClick={copiarID} style={{ marginLeft: 8 }}>Copiar</button>
        </li>
        <li><strong>Denominação:</strong> {certificate.denominação || "—"}</li>
        <li><strong>Proprietário(a):</strong> {certificate.proprietario || "—"}</li>
        <li><strong>Matrícula do imóvel:</strong> {certificate.matricula_imovel || "—"}</li>
        <li><strong>Natureza da Área:</strong> {certificate.natureza_area || "—"}</li>
        <li><strong>CNPJ:</strong> {certificate.cnpj || "—"}</li>
        <li><strong>Município/UF:</strong> {certificate.municipio_uf || "—"}</li>
        {/* liste os demais campos conforme quiser */}
      </ul>

      {points && points.length > 0 && (
        <>
          <h4>Pontos nesta certificação</h4>
          <ul style={{ maxHeight: 200, overflowY: "auto", fontFamily: "monospace" }}>
            {points.map((p) => (
              <li key={p.code}>{p.code}</li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}
