import React, { useState } from "react";
import API from "../api";

export default function Search() {
  const [code, setCode] = useState("");
  const [cert, setCert] = useState("");
  const [result, setResult] = useState(null);

  const searchCode = async () => {
    try {
      const res = await API.get(`/search/point/${code}`);
      setResult({ type: "point", data: res.data });
    } catch (e) {
      alert("Não encontrado");
    }
  };

  const searchCert = async () => {
    try {
      const res = await API.get(`/search/certification/${cert}`);
      setResult({ type: "cert", data: res.data });
    } catch (e) {
      alert("Não encontrado");
    }
  };

  return (
    <div style={{ marginTop: 20 }}>
      <div style={{ marginBottom: 10 }}>
        <input
          placeholder="Buscar ponto (ex: D5Y-M-0001)"
          value={code}
          onChange={(e) => setCode(e.target.value)}
        />
        <button onClick={searchCode}>Buscar ponto</button>
      </div>
      <div style={{ marginBottom: 10 }}>
        <input
          placeholder="Buscar certificação (ID)"
          value={cert}
          onChange={(e) => setCert(e.target.value)}
        />
        <button onClick={searchCert}>Buscar certificação</button>
      </div>
      {result && (
        <div style={{ marginTop: 10, border: "1px solid #ddd", padding: 10 }}>
          <pre>{JSON.stringify(result, null, 2)}</pre>
        </div>
      )}
    </div>
  );
}
