// frontend/src/components/Search.js

import React, { useState } from "react";
import API from "../api";
import { showToast } from "../toast";

export default function Search({ onPoint, onCertification }) {
  const [point, setPoint] = useState("");
  const [cert, setCert] = useState("");
  const [showAll, setShowAll] = useState(false);
  const [certList, setCertList] = useState([]);
  const [loadingList, setLoadingList] = useState(false);

  // busca ponto único
  const buscarPonto = async () => {
    if (!point) return;
    try {
      const res = await API.get(`/search/point/${encodeURIComponent(point)}`);
      onPoint(res.data);
    } catch (e) {
      showToast(e.response?.data?.detail || "Ponto não encontrado");
    }
  };

  // busca certificação única
  const buscarCert = async () => {
    if (!cert) return;
    try {
      const res = await API.get(
        `/search/certification/${encodeURIComponent(cert)}`
      );
      onCertification(res.data);
    } catch (e) {
      showToast(e.response?.data?.detail || "Certificação não encontrada");
    }
  };

  // carrega todas as certificações
  const toggleAll = async () => {
    if (!showAll) {
      setLoadingList(true);
      try {
        // consome o novo endpoint
        const res = await API.get("/certifications");
        setCertList(res.data);
      } catch {
        showToast("Falha ao carregar lista de certificados");
      } finally {
        setLoadingList(false);
      }
    }
    setShowAll((v) => !v);
  };

  // usuário clica em um resumo da lista
  const handleClickCert = (c) => {
    onCertification(c);
    setShowAll(false);
  };

  return (
    <div
      style={{
        marginTop: 16,
        padding: 16,
        border: "1px solid var(--color-border)",
        borderRadius: "var(--radius)",
        background: "var(--color-bg-alt)",
      }}
    >
      <h2>Pesquisar</h2>

      {/* busca ponto */}
      <div style={{ display: "flex", gap: 8, marginBottom: 12 }}>
        <input
          style={{ flex: 1, minWidth: 240, padding: 6 }}
          placeholder="Buscar ponto (ex: D5Y-M-0001)"
          value={point}
          onChange={(e) => setPoint(e.target.value)}
        />
        <button onClick={buscarPonto}>Buscar ponto</button>
      </div>

      {/* busca certificação + ver todas */}
      <div style={{ display: "flex", gap: 8, marginBottom: 12 }}>
        <input
          style={{ flex: 1, minWidth: 240, padding: 6 }}
          placeholder="Buscar certificação (ID)"
          value={cert}
          onChange={(e) => setCert(e.target.value)}
        />
        <button onClick={buscarCert}>Buscar certificação</button>
        <button onClick={toggleAll}>
          {showAll ? "Ocultar todas" : "Ver todas"}
        </button>
      </div>

      {/* lista completa */}
      {showAll && (
        <div
          style={{
            maxHeight: 200,
            overflowY: "auto",
            border: "1px solid var(--color-border)",
            borderRadius: 4,
            background: "var(--color-bg)",
          }}
        >
          {loadingList ? (
            <div style={{ padding: 8 }}>Carregando…</div>
          ) : certList.length > 0 ? (
            certList.map((c) => (
              <div
                key={c.certification_id}
                style={{
                  padding: 8,
                  borderBottom: "1px solid var(--color-border)",
                  cursor: "pointer",
                }}
                onClick={() => handleClickCert(c)}
              >
                <strong>{c.certification_id}</strong>{" "}
                {c.denominacao && <>— {c.denominacao}</>}
              </div>
            ))
          ) : (
            <div
              style={{
                padding: 8,
                color: "var(--color-text-light)",
                fontStyle: "italic",
              }}
            >
              Nenhum certificado cadastrado.
            </div>
          )}
        </div>
      )}
    </div>
  );
}
