import React, { useState } from "react";

export default function IntervalRow({ prefix, interval, nextInterval }) {
  const [expanded, setExpanded] = useState(false);

  const expandList = () => {
    setExpanded((e) => !e);
  };

  const generateCodes = (from, to) => {
    const [_, numA] = from.split("-");
    const [__, numB] = to.split("-");
    const a = parseInt(numA, 10);
    const b = parseInt(numB, 10);
    const codes = [];
    for (let i = a; i <= b; i++) {
      codes.push(`${prefix}-${String(i).padStart(4, "0")}`);
    }
    return codes;
  };

  return (
    <div
      style={{
        border: "1px solid #ccc",
        padding: 8,
        marginBottom: 8,
        display: "flex",
        alignItems: "center",
        gap: 12,
        justifyContent: "space-between",
      }}
    >
      <div>
        <strong>{prefix}</strong> disponíveis: {interval.from} até {interval.to}
      </div>
      <div style={{ display: "flex", gap: 8 }}>
        {nextInterval.from && (
          <button onClick={() => alert(`Próximo: ${nextInterval.from} até ${nextInterval.to}`)}>
            Próximo
          </button>
        )}
        <button onClick={expandList}>
          {expanded ? "Ocultar códigos" : "Expandir"}
        </button>
      </div>
      {expanded && (
        <div style={{ marginTop: 8, width: "100%" }}>
          <textarea
            readOnly
            style={{ width: "100%", height: 100 }}
            value={generateCodes(interval.from, interval.to).join("\n")}
          />
        </div>
      )}
    </div>
  );
}
