// src/App.js
import React, { useState, useEffect, useCallback } from "react";
import { Routes, Route, Link, useNavigate } from "react-router-dom";
import Upload from "./components/Upload";
import IntervalRow from "./components/IntervalRow";
import Search from "./components/Search";
import PointInfo from "./components/PointInfo";
import CertificateInfo from "./components/CertificateInfo";
import CertificatesList from "./components/CertificatesList";
import CertificateDetail from "./components/CertificateDetail";
import API from "./api";

export default function App() {
  const prefixes = ["D5Y-M", "D5Y-P", "D5Y-V"];
  const [stats, setStats] = useState(null);
  const [intervals, setIntervals] = useState({});
  const navigate = useNavigate();

  // --- Fetch stats ---
  const fetchStats = useCallback(async () => {
    try {
      const r = await API.get("/stats");
      setStats(r.data);
    } catch (e) {
      console.error("Erro ao buscar stats", e);
    }
  }, []);

  // --- Fetch intervals por prefixo ---
  const fetchIntervals = useCallback(async () => {
    const res = {};
    for (const p of prefixes) {
      try {
        const r = await API.get(`/intervals/${p}`);
        res[p] = r.data;
      } catch {
        res[p] = null;
      }
    }
    setIntervals(res);
  }, []);

  // Na montagem, busca stats e intervals
  useEffect(() => {
    fetchStats();
    fetchIntervals();
  }, [fetchStats, fetchIntervals]);

  // Handler ao subir PDF: atualiza tudo e volta para /
  const handleUploaded = async () => {
    await Promise.all([fetchStats(), fetchIntervals()]);
    navigate("/");
  };

  return (
    <div className="container">
      <header>
        <h1>SIGEF Manager</h1>
      </header>

      <Routes>
        {/* 1) Tela inicial */}
        <Route
          path="/"
          element={
            <>
              <Upload onUploaded={handleUploaded} />

              <div className="card" style={{ display: "flex", alignItems: "center", gap: 8 }}>
                <strong>NÃºmero de certificados:</strong>
                <span>{stats?.total_certificados ?? 0}</span>
                <Link to="/certificates">
                  <button>Ver certificados</button>
                </Link>
              </div>

              {prefixes.map((p) =>
                intervals[p] ? (
                  <IntervalRow
                    key={p}
                    prefix={p}
                    missingIntervals={intervals[p].missing_intervals}
                    currentInterval={intervals[p].current_interval}
                    nextInterval={intervals[p].next_interval}
                  />
                ) : null
              )}

              <Search />
            </>
          }
        />

        {/* 2) Lista de certificados */}
        <Route path="/certificates" element={<CertificatesList />} />

        {/* 3) Detalhe de um certificado */}
        <Route path="/certificates/:id" element={<CertificateDetail />} />
      </Routes>
    </div>
  );
}
