import React, { useState, useEffect } from "react";
import Upload from "./components/Upload";
import IntervalRow from "./components/IntervalRow";
import Search from "./components/Search";
import API from "./api";

export default function App() {
  const [stats, setStats] = useState(null);
  const [intervals, setIntervals] = useState({});
  const prefixes = ["D5Y-M", "D5Y-P", "D5Y-V"];

  const fetchIntervals = async () => {
    const res = {};
    for (const p of prefixes) {
      try {
        const r = await API.get(`/intervals/${p}`);
        res[p] = r.data;
      } catch (e) {
        res[p] = null;
      }
    }
    setIntervals(res);
  };

  useEffect(() => {
    const loadStats = async () => {
      try {
        const r = await API.get("/stats");
        setStats(r.data);
      } catch {}
    };
    loadStats();
    fetchIntervals();
  }, []);

  return (
    <div style={{ padding: 20, fontFamily: "Arial, sans-serif" }}>
      <h1>SIGEF Manager</h1>
      <Upload onUploaded={(data) => setStats(data)} />
      <div>
        <strong>Número de certificados: </strong>
        {stats ? stats.total_certificados : "—"}
      </div>
      <div style={{ marginTop: 20 }}>
        {prefixes.map((p) => (
          <div key={p}>
            {intervals[p] ? (
<IntervalRow
  prefix={p}
  missingIntervals={intervals[p]?.missing_intervals || []}
  currentInterval={intervals[p]?.current_interval || {}}
  nextInterval={intervals[p]?.next_interval || {}}
/>


            ) : (
              <div>{p}: carregando...</div>
            )}
          </div>
        ))}
      </div>
      <Search />
    </div>
  );
}
