import React, { useEffect, useState } from "react";
import Upload from "./components/Upload";
import Search from "./components/Search";
import IntervalRow from "./components/IntervalRow";
import API from "./api";

function App() {
  const prefixes = ["D5Y-M", "D5Y-P", "D5Y-V"];
  const [intervals, setIntervals] = useState({});
  const [stats, setStats] = useState({});

  const fetchIntervals = async () => {
    const res = {};
    for (const p of prefixes) {
      try {
        const r = await API.get(`/intervals/${p}`);
        res[p] = r.data;
      } catch {}
    }
    setIntervals(res);
  };

  const fetchStats = async () => {
    try {
      const r = await API.get("/stats");
      setStats(r.data);
    } catch {}
  };

  useEffect(() => {
    fetchIntervals();
    fetchStats();
  }, []);

  const handleUploaded = () => {
    fetchIntervals();
    fetchStats();
  };

  return (
    <div style={{ padding: 16 }}>
      <h1>SIGEF Manager</h1>
      <Upload onUploaded={handleUploaded} />
      <div>
        <div>
          NÃºmero de certificados: {stats.certificates_count || 0}
        </div>
        {prefixes.map((p) => (
          <IntervalRow
            key={p}
            prefix={p}
            missingIntervals={intervals[p]?.missing_intervals || []}
            currentInterval={intervals[p]?.current_interval || {}}
            nextInterval={intervals[p]?.next_interval || {}}
          />
        ))}
      </div>
      <Search
        onPoint={(data) => {
          console.log("resultado ponto", data);
        }}
        onCertification={(data) => {
          console.log("resultado cert", data);
        }}
      />
    </div>
  );
}

export default App;
