// src/components/Search.js
import React, { useState } from "react";
import API from "../api";
import { showToast } from "../toast";

export default function Search({ onPoint, onCertification }) {
  const [point, setPoint] = useState("");
  const [cert, setCert] = useState("");
  const [displayedCerts, setDisplayedCerts] = useState([]);

  const buscarPonto = async () => {
    if (!point) return;
    try {
      const res = await API.get(`/search/point/${encodeURIComponent(point)}`);
      onPoint(res.data);
      setDisplayedCerts([]);
    } catch (e) {
      showToast(e.response?.data?.detail || "Ponto não encontrado");
    }
  };

  const buscarCert = async (certId = null) => {
    const query = certId ?? cert;
    if (!query) return;
    try {
      const res = await API.get(
        `/search/certification/${encodeURIComponent(query)}`
      );
      onCertification(res.data);
      setDisplayedCerts([]);
    } catch (e) {
      showToast(e.response?.data?.detail || "Certificação não encontrada");
    }
  };

  // Busca todos os certificados via endpoint /stats
  const handleShowAll = async () => {
    try {
      const res = await API.get("/stats");
      const list = res.data.certificates;
      if (Array.isArray(list) && list.length > 0) {
        setDisplayedCerts(list);
      } else {
        showToast("Nenhum certificado disponível");
      }
    } catch (e) {
      showToast("Erro ao obter certificados");
    }
  };

  return (
    <div
      style={{
        marginTop: 16,
        padding: 16,
        border: "1px solid #ddd",
        borderRadius: 6,
      }}
    >
      <h2>Pesquisar</h2>
      <div style={{ marginBottom: 8 }}>
        <input
          style={{ width: 240, padding: 6 }}
          placeholder="Buscar ponto (ex: D5Y-M-0001)"
          value={point}
          onChange={(e) => setPoint(e.target.value)}
        />
        <button
          onClick={buscarPonto}
          style={{ marginLeft: 8, padding: "6px 12px" }}
        >
          Buscar ponto
        </button>
      </div>
      <div style={{ marginBottom: 8 }}>
        <input
          style={{ width: 240, padding: 6 }}
          placeholder="Buscar certificação (ID)"
          value={cert}
          onChange={(e) => setCert(e.target.value)}
        />
        <button
          onClick={() => buscarCert()}
          style={{ marginLeft: 8, padding: "6px 12px" }}
        >
          Buscar certificação
        </button>
        <button
          onClick={handleShowAll}
          style={{ marginLeft: 8, padding: "6px 12px" }}
        >
          Ver todos os certificados
        </button>
      </div>
      {displayedCerts.length > 0 && (
        <ul style={{ marginTop: 12 }}>
          {displayedCerts.map((certId) => (
            <li key={certId}>
              <button
                style={{
                  background: "none",
                  border: "none",
                  padding: 0,
                  color: "#04c",
                  cursor: "pointer",
                }}
                onClick={() => buscarCert(certId)}
              >
                {certId}
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
