// src/components/Search.js
import React, { useState } from "react";
import API from "../api";
import { showToast } from "../toast";

export default function Search({ onPoint, onCertification }) {
  const [point, setPoint] = useState("");
  const [cert, setCert] = useState("");
  const [allCerts, setAllCerts] = useState(null);
  const [loadingAll, setLoadingAll] = useState(false);

  const buscarPonto = async () => {
    if (!point) return;
    try {
      const res = await API.get(`/search/point/${encodeURIComponent(point)}`);
      onPoint(res.data);
    } catch (e) {
      showToast(e.response?.data?.detail || "Ponto não encontrado");
    }
  };

  const buscarCert = async () => {
    if (!cert) return;
    try {
      const res = await API.get(`/search/certification/${encodeURIComponent(cert)}`);
      onCertification(res.data);
    } catch (e) {
      showToast(e.response?.data?.detail || "Certificação não encontrada");
    }
  };

  const verTodas = async () => {
    setLoadingAll(true);
    try {
      const res = await API.get("/certifications");
      setAllCerts(res.data);
    } catch (e) {
      showToast("Falha ao carregar todas as certificações");
    } finally {
      setLoadingAll(false);
    }
  };

  const ocultarTodas = () => {
    setAllCerts(null);
  };

  return (
    <div className="card">
      <h2>Pesquisar</h2>
      <div style={{ display: "flex", gap: "8px", marginBottom: "8px" }}>
        <input
          type="text"
          placeholder="Buscar ponto (ex: D5Y-M-0001)"
          value={point}
          onChange={(e) => setPoint(e.target.value)}
        />
        <button onClick={buscarPonto}>Buscar ponto</button>
      </div>
      <div style={{ display: "flex", gap: "8px", alignItems: "center" }}>
        <input
          type="text"
          placeholder="Buscar certificação (ID)"
          value={cert}
          onChange={(e) => setCert(e.target.value)}
        />
        <button onClick={buscarCert}>Buscar certificação</button>
        {allCerts ? (
          <button onClick={ocultarTodas}>Ocultar todas</button>
        ) : (
          <button onClick={verTodas} disabled={loadingAll}>
            {loadingAll ? "Carregando..." : "Ver todas"}
          </button>
        )}
      </div>

      {allCerts && (
        <div style={{ marginTop: "16px" }}>
          {allCerts.length === 0 ? (
            <p>Nenhum certificado cadastrado.</p>
          ) : (
            <ul style={{ listStyle: "none", padding: 0, margin: 0 }}>
              {allCerts.map((c) => (
                <li
                  key={c.certification_id}
                  style={{
                    padding: "8px",
                    borderBottom: "1px solid var(--color-border)",
                    cursor: "pointer",
                  }}
                  onClick={() => onCertification(c.certification_id)}
                >
                  <strong>{c.certification_id}</strong>
                  {c.denominação && ` — ${c.denominação}`}
                </li>
              ))}
            </ul>
          )}
        </div>
      )}
    </div>
  );
}
