// frontend/src/App.js
import React, { useState, useEffect } from "react";
import { Routes, Route, Link } from "react-router-dom";
import Upload from "./components/Upload";
import IntervalRow from "./components/IntervalRow";
import Search from "./components/Search";
import CertificateList from "./components/CertificateList";
import CertificateDetail from "./components/CertificateDetail";
import API from "./api";

export default function App() {
  const [stats, setStats] = useState(null);
  const [intervals, setIntervals] = useState({});
  const prefixes = ["D5Y-M", "D5Y-P", "D5Y-V"];

  const fetchIntervals = async () => {
    const res = {};
    for (const p of prefixes) {
      try {
        const r = await API.get(`/intervals/${p}`);
        res[p] = r.data;
      } catch {
        res[p] = null;
      }
    }
    setIntervals(res);
  };

  useEffect(() => {
    API.get("/stats").then(r => setStats(r.data));
    fetchIntervals();
  }, []);

  return (
    <div className="container">
      <h1>SIGEF Manager</h1>
      <Routes>
        {/* página principal */}
        <Route path="/" element={
          <>
            <Upload onUploaded={d => setStats(d)} />
            <div className="card">
              <strong>Número de certificados:</strong>{" "}
              {stats?.total_certificados ?? "—"}
              {" "}
              <Link to="/certifications">
                <button>Ver lista</button>
              </Link>
            </div>
            {prefixes.map(p =>
              <IntervalRow
                key={p}
                prefix={p}
                missingIntervals={intervals[p]?.missing_intervals || []}
                currentInterval={intervals[p]?.current_interval || {}}
                nextInterval={intervals[p]?.next_interval || {}}
              />
            )}
            <Search />
          </>
        }/>

        {/* listagem de certificados */}
        <Route path="/certifications" element={<CertificateList />} />
        {/* detalhe de um certificado */}
        <Route path="/certifications/:cert_id" element={<CertificateDetail />} />
      </Routes>
    </div>
  );
}
