import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import API from "../api";

export default function CertificatesList() {
  const [all, setAll] = useState([]);
  const [filter, setFilter] = useState("");

  useEffect(() => {
    API.get("/certifications").then((r) => setAll(r.data));
  }, []);

  const shown = all.filter((c) =>
    c.certification_id.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="card">
      <h2>Todos os Certificados</h2>
      <input
        type="text"
        placeholder="Pesquisar ID..."
        value={filter}
        onChange={(e) => setFilter(e.target.value)}
      />
      <ul style={{ marginTop: 8 }}>
        {shown.map((c) => (
          <li key={c.certification_id} style={{ marginBottom: 4 }}>
            <Link to={`/certificates/${c.certification_id}`}>
              {c.certification_id}
            </Link>
          </li>
        ))}
        {shown.length === 0 && <li><em>Nenhum encontrado.</em></li>}
      </ul>
      <Link to="/">
        <button>Voltar</button>
      </Link>
    </div>
  );
}
