// src/components/IntervalRow.js

import React, { useState, useMemo, useCallback } from "react";
import { showToast } from "../toast"; // seu utilitário de toast

export default function IntervalRow({
  prefix,
  missingIntervals = [],
  currentInterval,
  nextInterval,
}) {
  // Determina índice inicial (-1 = sugestão)
  const findInitialIndex = () => {
    if (!currentInterval?.from) return -1;
    const idx = missingIntervals.findIndex(
      (m) => m.from === currentInterval.from && m.to === currentInterval.to
    );
    return idx >= 0 ? idx : -1;
  };

  const initialIndex = findInitialIndex();
  const [activeGapIndex, setActiveGapIndex] = useState(initialIndex);
  const [expanded, setExpanded] = useState(false);
  const [showAll, setShowAll] = useState(false);
  const [history, setHistory] = useState([initialIndex]);
  const [historyPos, setHistoryPos] = useState(0);

  // Histórico de navegação
  const pushHistory = useCallback(
    (newIndex) => {
      const slice = history.slice(0, historyPos + 1);
      slice.push(newIndex);
      setHistory(slice);
      setHistoryPos(slice.length - 1);
    },
    [history, historyPos]
  );

  // Ativa gap e reseta “showAll”
  const setActive = (idx) => {
    setActiveGapIndex(idx);
    pushHistory(idx);
    setShowAll(false);
  };

  const goPrevHistory = () => {
    if (historyPos > 0) {
      setHistoryPos((p) => p - 1);
      setActiveGapIndex(history[historyPos - 1]);
      setShowAll(false);
    }
  };

  const reset = () => {
    setActiveGapIndex(initialIndex);
    setHistory([initialIndex]);
    setHistoryPos(0);
    setShowAll(false);
  };

  // Intervalo ativo (gap ou sugestão)
  const activeInterval = useMemo(() => {
    if (activeGapIndex === -1) return currentInterval || {};
    return missingIntervals[activeGapIndex] || currentInterval || {};
  }, [activeGapIndex, missingIntervals, currentInterval]);

  const isSuggested =
    activeGapIndex === -1 ||
    !missingIntervals.some(
      (m) =>
        m.from === activeInterval.from && m.to === activeInterval.to
    );

  const computeHasNext = () =>
    activeGapIndex === -1
      ? missingIntervals.length > 0
      : activeGapIndex + 1 < missingIntervals.length;

  const computeHasPrev = () => historyPos > 0;

  const handleNext = () => {
    if (activeGapIndex === -1 && missingIntervals.length > 0) {
      setActive(0);
    } else if (activeGapIndex + 1 < missingIntervals.length) {
      setActive(activeGapIndex + 1);
    }
  };

  const handlePrev = () => {
    if (computeHasPrev()) goPrevHistory();
  };

  // Extrai número do código “D5Y-M-0001” → 1
  const extractNumber = (code) =>
    parseInt(code?.split("-").pop() || "", 10);

  // Gera lista de códigos de “from” a “to”
  const generateCodes = (from, to) => {
    const a = extractNumber(from);
    const b = extractNumber(to);
    if (isNaN(a) || isNaN(b) || b < a) return [];
    const arr = [];
    for (let i = a; i <= b; i++) {
      arr.push(`${prefix}-${String(i).padStart(4, "0")}`);
    }
    return arr;
  };

  // Códigos do intervalo ativo e flag “grande”
  const activeCodes = useMemo(
    () => generateCodes(activeInterval.from, activeInterval.to),
    [activeInterval, prefix]
  );
  const tooBig = activeCodes.length > 100;

  // Copiar com toast
  const handleCopyActive = () => {
    navigator.clipboard.writeText(activeCodes.join("\n"));
    showToast("Intervalo ativo copiado!");
  };
  const handleCopyGap = (from, to) => {
    const codes = generateCodes(from, to);
    navigator.clipboard.writeText(codes.join("\n"));
    showToast("Códigos do gap copiados!");
  };

  return (
    <div className="interval-row">
      {/* Cabeçalho */}
      <div className="interval-info">
        <strong>{prefix}</strong> disponíveis:{" "}
        {activeInterval.from || "—"} até {activeInterval.to || "—"}{" "}
        {isSuggested && <em>(próximo disponível)</em>}
      </div>
      <div className="actions">
        <button onClick={handlePrev} disabled={!computeHasPrev()}>
          Voltar
        </button>
        {computeHasNext() && <button onClick={handleNext}>Próximo</button>}
        <button onClick={reset}>Reset</button>
        <button onClick={() => setExpanded((e) => !e)}>
          {expanded ? "Ocultar" : "Expandir"}
        </button>
      </div>

      {/* Expansão */}
      {expanded && (
        <div className="interval-details">
          {/* Gaps reais */}
          <div className="gaps-section">
            <strong>Gaps reais:</strong>
            {missingIntervals.length === 0 && (
              <p><em>Nenhum gap real.</em></p>
            )}
            {missingIntervals.map((g, i) => (
              <div
                key={`${g.from}-${g.to}`}
                className={
                  i === activeGapIndex
                    ? "gap-row gap-active"
                    : "gap-row"
                }
              >
                <span>
                  {g.from} até {g.to}{" "}
                  {i === 0 && <em>(current)</em>}{" "}
                  {i === 1 && <em>(next)</em>}
                </span>
                <div className="gap-actions">
                  <button onClick={() => setActive(i)}>Usar</button>
                  <button onClick={() => handleCopyGap(g.from, g.to)}>
                    Copiar
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* Intervalo ativo */}
          <div className="active-section">
            <div className="active-header">
              <strong>Intervalo ativo:</strong>
              <button onClick={handleCopyActive}>Copiar tudo</button>
              <span>({activeCodes.length} códigos)</span>
            </div>

            {/* Mostrar todos / prévia */}
            {tooBig && !showAll ? (
              <div className="preview-block">
                <p>
                  Intervalo com {activeCodes.length} códigos.{" "}
                  <button onClick={() => setShowAll(true)}>
                    Mostrar todos
                  </button>
                </p>
                <textarea
                  readOnly
                  value={
                    activeCodes.slice(0, 10).join("\n") + "\n..."
                  }
                />
              </div>
            ) : (
              <textarea
                readOnly
                value={activeCodes.join("\n")}
              />
            )}

            {/* Botão “Mostrar menos” */}
            {tooBig && showAll && (
              <button onClick={() => setShowAll(false)}>
                Mostrar menos
              </button>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
