// src/App.js

import React, { useState, useEffect, useCallback } from "react";
import Upload from "./components/Upload";
import IntervalRow from "./components/IntervalRow";
import Search from "./components/Search";
import PointInfo from "./components/PointInfo";
import CertificateInfo from "./components/CertificateInfo";
import API from "./api";

export default function App() {
  const prefixes = ["D5Y-M", "D5Y-P", "D5Y-V"];
  const [stats, setStats] = useState({ total_certificados: 0 });
  const [intervals, setIntervals] = useState({});
  const [pointResult, setPointResult] = useState(null);
  const [certResult, setCertResult] = useState(null);

  // Busca total de certificados
  const fetchStats = useCallback(async () => {
    try {
      const res = await API.get("/stats");
      setStats(res.data);
    } catch (err) {
      console.error("Erro ao buscar stats:", err);
    }
  }, []);

  // Busca intervalos para cada prefixo
  const fetchIntervals = useCallback(async () => {
    const data = {};
    for (const prefix of prefixes) {
      try {
        const res = await API.get(`/intervals/${prefix}`);
        data[prefix] = res.data;
      } catch {
        data[prefix] = null;
      }
    }
    setIntervals(data);
  }, []);

  // Ao montar, carrega stats e intervalos
  useEffect(() => {
    fetchStats();
    fetchIntervals();
  }, [fetchStats, fetchIntervals]);

  // Ao subir um PDF, recarrega tudo
  const handleUploaded = async () => {
    setPointResult(null);
    setCertResult(null);
    await Promise.all([fetchStats(), fetchIntervals()]);
  };

  return (
    <div className="container">
      <header style={{ marginBottom: "1.5rem" }}>
        <h1>SIGEF Manager</h1>
      </header>

      <section className="card upload-form">
        <Upload onUploaded={handleUploaded} />
      </section>

      <section style={{ marginBottom: "1.5rem" }}>
        <strong>NÃºmero de certificados:</strong>{" "}
        {stats.total_certificados ?? 0}
      </section>

      <section>
        {prefixes.map((prefix) => {
          const cfg = intervals[prefix];
          if (!cfg) return null;
          return (
            <IntervalRow
              key={prefix}
              prefix={prefix}
              missingIntervals={cfg.missing_intervals}
              currentInterval={cfg.current_interval}
              nextInterval={cfg.next_interval}
            />
          );
        })}
      </section>

      {/* pesquisa */}
      <section className="card" style={{ marginTop: "2rem" }}>
        <Search
          onPoint={(data) => {
            setPointResult(data);
            setCertResult(null);
          }}
          onCertification={(data) => {
            setCertResult(data);
            setPointResult(null);
          }}
        />
      </section>

      {/* exibe resultado de busca */}
      {pointResult && (
        <section className="card" style={{ marginTop: "1.5rem" }}>
          <PointInfo data={pointResult} />
        </section>
      )}
      {certResult && (
        <section className="card" style={{ marginTop: "1.5rem" }}>
          <CertificateInfo data={certResult} />
        </section>
      )}
    </div>
  );
}
